<template>
    <div class="profile-list-item">
        <label>{{ label }}: </label>
        <a href="" class="firstname-link" v-if="!show">
            {{ fieldData }}
        </a>
        <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 512 512" class="pen" v-if="!show" v-on:click="edit">
            <image id="Слой_1" data-name="Слой 1" x="85" y="75" width="363" height="362" xlink:href="data:img/png;base64,"/>
        </svg>
        <input type="text" class="profile-input" v-else v-model="fieldData">
        <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 512 512" class="yes" v-if="icons" v-on:click="save">
            <image id="Слой_1" data-name="Слой 1" x="47" y="99" width="418" height="314" xlink:href="data:img/png;base64,"/>
        </svg>
        <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"  viewBox="0 0 512 512" class="no" v-if="icons" v-on:click="cancel">
            <image id="Слой_1" data-name="Слой 1" x="89" y="88" width="335" height="336" xlink:href="data:img/png;base64,"/>
        </svg>
    </div>
</template>

<script>
export default {
    name: "ProfileComponent",
    props: {
        label: {
            type: String,
            required: true
        },
        field: {
            type: String,
            required: true,
        },
        fieldName: {
            type: String,
            required: true
        },
        uid: {
            type: Number,
            required: true
        }
    },
    data(){
        return {
            fieldData: this.field,
            show: false,
            icons: false
        }
    },
    methods: {
        edit(){
            this.show = true;
            this.icons = true;
        },
        cancel(){
            this.show = false;
            this.icons = false;
        },
        save()
        {
            axios.post('/api/profile', {
                info: {
                    data: this.fieldData,
                    id: this.uid,
                    filedName: this.fieldName
                }
            })
            .then(() => {
                this.show = false;
                this.icons = false;
            });
        }
    }
}
</script>

<style scoped>
    label{
        width: calc(100% - 360px);
    }
</style>
